css:
html,
body,
.tree-view,
.tooltip,
.tab-bar .tab,
.find-and-replace .find-meta-container,
atom-text-editor[mini],
.btn,
.status-bar,
linter-message {
  font-size: 1rem;
}
.editor .invisible-character,
atom-text-editor::shadow .invisible-character {
  color: #333333;
}
.editor.is-focused .line.cursor-line {
  background: transparent;
}
