css:
atom-text-editor::shadow atom-text-editor-minimap.scrolling,
atom-text-editor::shadow atom-text-editor-minimap:hover {
  left: 90%;
  opacity: 1;
}
atom-text-editor::shadow atom-text-editor-minimap {
  background: rgba(0, 0, 0, 0.2);
  position: absolute;
  left: 100%;
  transition: left 0.1s, opacity 0.2s;
  opacity: 0.05;
}
